import { createContext,useState } from "react";


const PostContext = createContext();

function HandleContext({children}) {

 
    return(
       <PostContext.Provider value={{
        step,
        setStep,
        test,
        setTest,
        isopen,
        setIsOpen
       }}>
        {children}
       </PostContext.Provider>
    )
}

export {HandleContext}
